import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  ScrollView,
  RefreshControl,
  TouchableOpacity,
} from 'react-native';
import { Ionicons } from '@expo/vector-icons';
import { LinearGradient } from 'expo-linear-gradient';

const LiveScreen = () => {
  const [refreshing, setRefreshing] = useState(false);
  const [selectedMeter, setSelectedMeter] = useState(0);

  const metersData = [
    {
      id: 1,
      name: 'Meter 1',
      voltage: 230,
      current: 5.2,
      power: 1200,
      energy: 50,
      powerFactor: 0.95,
      frequency: 50,
      harmonics: 3.2,
      status: 'Online',
    },
    {
      id: 2,
      name: 'Meter 2',
      voltage: 225,
      current: 4.8,
      power: 1080,
      energy: 45,
      powerFactor: 0.92,
      frequency: 50.2,
      harmonics: 2.8,
      status: 'Online',
    },
    {
      id: 3,
      name: 'Meter 3',
      voltage: 235,
      current: 6.1,
      power: 1435,
      energy: 62,
      powerFactor: 0.97,
      frequency: 49.8,
      harmonics: 3.5,
      status: 'Online',
    },
    {
      id: 4,
      name: 'Meter 4',
      voltage: 228,
      current: 5.5,
      power: 1254,
      energy: 55,
      powerFactor: 0.94,
      frequency: 50.1,
      harmonics: 3.0,
      status: 'Offline',
    },
  ];

  const onRefresh = () => {
    setRefreshing(true);
    // Simulate API call
    setTimeout(() => {
      setRefreshing(false);
    }, 2000);
  };

  const getParameterIcon = (parameter) => {
    const icons = {
      voltage: 'flash',
      current: 'trending-up',
      power: 'speedometer',
      energy: 'battery-charging',
      powerFactor: 'analytics',
      frequency: 'pulse',
      harmonics: 'radio',
    };
    return icons[parameter] || 'information-circle';
  };

  const getParameterColor = (parameter) => {
    const colors = {
      voltage: '#3b82f6',
      current: '#10b981',
      power: '#f59e0b',
      energy: '#8b5cf6',
      powerFactor: '#ef4444',
      frequency: '#06b6d4',
      harmonics: '#f97316',
    };
    return colors[parameter] || '#6b7280';
  };

  const renderMeterCard = (meter) => {
    const parameters = [
      { key: 'voltage', label: 'Voltage', value: `${meter.voltage} V`, unit: 'V' },
      { key: 'current', label: 'Current', value: `${meter.current} A`, unit: 'A' },
      { key: 'power', label: 'Power', value: `${meter.power} W`, unit: 'W' },
      { key: 'energy', label: 'Energy', value: `${meter.energy} kWh`, unit: 'kWh' },
      { key: 'powerFactor', label: 'Power Factor', value: meter.powerFactor.toString(), unit: '' },
      { key: 'frequency', label: 'Frequency', value: `${meter.frequency} Hz`, unit: 'Hz' },
      { key: 'harmonics', label: 'Harmonics', value: `${meter.harmonics} %`, unit: '%' },
    ];

    return (
      <View key={meter.id} style={styles.meterCard}>
        <View style={styles.meterHeader}>
          <View style={styles.meterTitleContainer}>
            <Ionicons name="speedometer-outline" size={24} color="#1a365d" />
            <Text style={styles.meterTitle}>{meter.name}</Text>
          </View>
          <View style={[
            styles.statusBadge,
            { backgroundColor: meter.status === 'Online' ? '#10b981' : '#ef4444' }
          ]}>
            <Text style={styles.statusText}>{meter.status}</Text>
          </View>
        </View>

        <View style={styles.parametersGrid}>
          {parameters.map((param, index) => (
            <View key={index} style={styles.parameterCard}>
              <View style={styles.parameterHeader}>
                <Ionicons
                  name={getParameterIcon(param.key)}
                  size={20}
                  color={getParameterColor(param.key)}
                />
                <Text style={styles.parameterLabel}>{param.label}</Text>
              </View>
              <Text style={[styles.parameterValue, { color: getParameterColor(param.key) }]}>
                {param.value}
              </Text>
            </View>
          ))}
        </View>
      </View>
    );
  };

  return (
    <View style={styles.container}>
      {/* Header */}
      <LinearGradient
        colors={['#1a365d', '#2d3748']}
        style={styles.header}
      >
        <Text style={styles.headerTitle}>Live Meter Readings</Text>
        <Text style={styles.headerSubtitle}>Real-time energy monitoring</Text>
        <TouchableOpacity style={styles.refreshButton} onPress={onRefresh}>
          <Ionicons name="refresh" size={20} color="#ffffff" />
          <Text style={styles.refreshText}>Refresh</Text>
        </TouchableOpacity>
      </LinearGradient>

      {/* Meters List */}
      <ScrollView
        style={styles.scrollView}
        showsVerticalScrollIndicator={false}
        refreshControl={
          <RefreshControl refreshing={refreshing} onRefresh={onRefresh} />
        }
      >
        {metersData.map((meter) => renderMeterCard(meter))}
      </ScrollView>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f8fafc',
  },
  header: {
    padding: 20,
    paddingBottom: 25,
  },
  headerTitle: {
    fontSize: 24,
    fontWeight: 'bold',
    color: '#ffffff',
    marginBottom: 5,
  },
  headerSubtitle: {
    fontSize: 16,
    color: '#a0aec0',
    marginBottom: 15,
  },
  refreshButton: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: 'rgba(255, 255, 255, 0.2)',
    paddingHorizontal: 15,
    paddingVertical: 8,
    borderRadius: 20,
    alignSelf: 'flex-start',
  },
  refreshText: {
    color: '#ffffff',
    marginLeft: 8,
    fontWeight: '600',
  },
  scrollView: {
    flex: 1,
    paddingHorizontal: 20,
  },
  meterCard: {
    backgroundColor: '#ffffff',
    borderRadius: 15,
    padding: 20,
    marginBottom: 20,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 8,
    elevation: 5,
  },
  meterHeader: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 20,
    paddingBottom: 15,
    borderBottomWidth: 1,
    borderBottomColor: '#e2e8f0',
  },
  meterTitleContainer: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  meterTitle: {
    fontSize: 20,
    fontWeight: 'bold',
    color: '#1a365d',
    marginLeft: 10,
  },
  statusBadge: {
    paddingHorizontal: 12,
    paddingVertical: 6,
    borderRadius: 20,
  },
  statusText: {
    fontSize: 12,
    fontWeight: '600',
    color: '#ffffff',
  },
  parametersGrid: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'space-between',
  },
  parameterCard: {
    width: '48%',
    backgroundColor: '#f7fafc',
    borderRadius: 12,
    padding: 15,
    marginBottom: 12,
    borderWidth: 1,
    borderColor: '#e2e8f0',
  },
  parameterHeader: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 8,
  },
  parameterLabel: {
    fontSize: 12,
    color: '#718096',
    fontWeight: '600',
    marginLeft: 8,
  },
  parameterValue: {
    fontSize: 18,
    fontWeight: 'bold',
  },
});

export default LiveScreen;
